mapper.select:
select p.id AS id, p.pid as pid, p.name as name, p.type as type, p.sort as sort, p.url as url, p.icon as icon, p.perm_code as permCode, p.description as description, p.state as state FROM permission p INNER JOIN role_permission rp on p.ID=rp.permission_id INNER JOIN role r ON r.id=rp.role_id INNER JOIN user_role ur ON ur.role_id =rp.role_id INNER JOIN user u ON u.id = ur.user_id
p.id = #{id}
and login_name = #{loginName}
and u.id = #{userId}
and p.type = #{type}

select DISTINCT (p.id) AS id, p.pid as pid, p.name as name, p.type as type, p.sort as sort, p.url as url, p.icon as icon, p.perm_code as permCode, p.description as description, p.state as state FROM permission p INNER JOIN role_permission rp on p.ID=rp.permission_id INNER JOIN role r ON r.id=rp.role_id INNER JOIN user_role ur ON ur.role_id =rp.role_id INNER JOIN user u ON u.id = ur.user_id
id = #{id}
and login_name = #{loginName}
and u.id = #{userId}
and p.type = #{type}

select
p.id AS id,
p.pid as pid,
p.name as name,
p.sort as sort,
p.url as url,
p.icon as icon,
p.perm_code as permCode,
p.description as description
FROM permission p


        id = #{id}


        and login_name = #{loginName}


        and u.id = #{userId}


        and p.type = #{type}


        and p.pid = #{pid}
